import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { libroService } from '../services/libroService';
import '../styles/pages/EditarLibro.css';

const EditarLibro = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [showError, setShowError] = useState(false);
  const [errors, setErrors] = useState({});
  const [formData, setFormData] = useState({
    titulo: '',
    autor: '',
    fecha_publicacion: '',
    genero: ''
  });

  useEffect(() => {
    const cargarLibro = async () => {
      try {
        const response = await libroService.getById(id);
        if (response && response.success) {
          setFormData(response.data);
        } else {
          setError('No se pudo cargar la información del libro');
        }
      } catch (err) {
        console.error('Error al cargar el libro:', err);
        setError('Error al cargar el libro');
      } finally {
        setLoading(false);
      }
    };

    cargarLibro();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Limpiar el mensaje de error del campo que se está modificando
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }

    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const validateForm = () => {
    const newErrors = {};
    let isValid = true;

    if (!formData.titulo.trim()) {
      newErrors.titulo = 'El título es requerido';
      isValid = false;
    }

    if (!formData.autor.trim()) {
      newErrors.autor = 'El autor es requerido';
      isValid = false;
    }

    if (!formData.fecha_publicacion) {
      newErrors.fecha_publicacion = 'La fecha de publicación es requerida';
      isValid = false;
    }

    if (!formData.genero.trim()) {
      newErrors.genero = 'El género es requerido';
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = async (e) => {
    // Prevenir el comportamiento por defecto del formulario
    if (e) {
      e.preventDefault();
      e.stopPropagation();
      e.returnValue = false;
    }

    if (!validateForm()) {
      return false; // Evitar envío del formulario si la validación falla
    }

    // Limpiar errores anteriores
    setError(null);
    setErrors({});

    try {
      const response = await libroService.update(id, formData);
      if (response && response.success) {
        setSuccess(true);
        setError(null);
        setShowError(false);

        // Ocultar el mensaje después de 2 segundos
        const timer = setTimeout(() => {
          setSuccess(false);
          clearTimeout(timer);
        }, 2000);

        // No hacemos navigate, la página permanece igual
        return false;
      } else {
        throw new Error('No se pudo actualizar el libro');
      }
    } catch (err) {
      console.error('Error al actualizar el libro:', err);

      // Manejar errores de validación del backend
      if (err.response && err.response.data) {
        if (err.response.data.message) {
          // Mostrar el mensaje de error del backend en el campo correspondiente
          // Asumimos que el backend devuelve el mensaje en err.response.data.message
          setErrors({
            ...errors,
            titulo: err.response.data.message
          });
        } else if (err.response.data.message) {
          // Para otros mensajes de error, mostrarlos como antes
          setError(err.response.data.message);
          setShowError(true);
        }
      } else {
        // Si es un mensaje de error genérico
        setError(err.message || 'Error al actualizar el libro');
        setShowError(true);
      }

      // Ocultar el mensaje de error después de 5 segundos
      setTimeout(() => {
        setShowError(false);
      }, 5000);

      return false; // Evitar cualquier comportamiento por defecto del formulario
    }
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Cargando...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger" role="alert">
        {error}
      </div>
    );
  }

  return (
    <div className="editar-libro">
      <div className="editar-card">
        <div className="editar-header">
          <h2>Editar Libro</h2>
          {success && (
            <div className="alert alert-success mt-2 mb-0">
              ¡Libro actualizado correctamente!
            </div>
          )}
          {showError && error && (
            <div className="alert alert-danger mt-2 mb-0" role="alert">
              {error}
              <button 
                type="button" 
                className="btn-close float-end" 
                onClick={() => setShowError(false)}
                aria-label="Cerrar"
              ></button>
            </div>
          )}
        </div>

        <div className="editar-body">

          <form onSubmit={handleSubmit} noValidate>
            <div className="mb-3">
              <label htmlFor="titulo" className="form-label">Título</label>
              <input
                type="text"
                className={`form-control ${errors.titulo ? 'is-invalid' : ''}`}
                id="titulo"
                name="titulo"
                value={formData.titulo}
                onChange={handleChange}
                required
              />
              {errors.titulo && <div className="invalid-feedback">{errors.titulo[0] || errors.titulo}</div>}
            </div>

            <div className="mb-3">
              <label htmlFor="autor" className="form-label">Autor</label>
              <input
                type="text"
                className={`form-control ${errors.autor ? 'is-invalid' : ''}`}
                id="autor"
                name="autor"
                value={formData.autor}
                onChange={handleChange}
                required
              />
              {errors.autor && <div className="invalid-feedback">{errors.autor[0] || errors.autor}</div>}
            </div>

            <div className="mb-3">
              <label htmlFor="fecha_publicacion" className="form-label">Fecha de Publicación</label>
              <input
                type="date"
                className={`form-control ${errors.fecha_publicacion ? 'is-invalid' : ''}`}
                id="fecha_publicacion"
                name="fecha_publicacion"
                value={formData.fecha_publicacion}
                onChange={handleChange}
                required
              />
              {errors.fecha_publicacion && <div className="invalid-feedback">{errors.fecha_publicacion[0] || errors.fecha_publicacion}</div>}
            </div>

            <div className="mb-4">
              <label htmlFor="genero" className="form-label">Género</label>
              <input
                type="text"
                className={`form-control ${errors.genero ? 'is-invalid' : ''}`}
                id="genero"
                name="genero"
                value={formData.genero}
                onChange={handleChange}
                required
              />
              {errors.genero && <div className="invalid-feedback">{errors.genero[0] || errors.genero}</div>}
            </div>

            <div className="d-flex justify-content-between">
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => navigate('/libros')}
              >
                Cancelar
              </button>
              <button type="submit" className="btn btn-warning text-white">
                Actualizar Libro
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default EditarLibro;
