import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { libroService } from '../services/libroService';
import '../styles/pages/EditarLibro.css';

const EditarLibro = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({
    titulo: '',
    autor: '',
    fecha_publicacion: '',
    genero: ''
  });

  useEffect(() => {
    const cargarLibro = async () => {
      try {
        const response = await libroService.getById(id);
        if (response && response.success) {
          setFormData(response.data);
        } else {
          setError('No se pudo cargar la información del libro');
        }
      } catch (err) {
        console.error('Error al cargar el libro:', err);
        setError('Error al cargar el libro');
      } finally {
        setLoading(false);
      }
    };

    cargarLibro();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await libroService.update(id, formData);
      if (response && response.success) {
        navigate(`/libros/${id}/detalles`);
      } else {
        setError('No se pudo actualizar el libro');
      }
    } catch (err) {
      console.error('Error al actualizar el libro:', err);
      setError('Error al actualizar el libro');
    }
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Cargando...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger" role="alert">
        {error}
      </div>
    );
  }

  return (
    <div className="editar-libro">
      <div className="editar-card">
        <div className="editar-header">
          <h2>Editar Libro</h2>
        </div>

        <div className="editar-body">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="titulo" className="form-label">Título</label>
              <input
                type="text"
                className="form-control"
                id="titulo"
                name="titulo"
                value={formData.titulo}
                onChange={handleChange}
                required
              />
            </div>

            <div className="mb-3">
              <label htmlFor="autor" className="form-label">Autor</label>
              <input
                type="text"
                className="form-control"
                id="autor"
                name="autor"
                value={formData.autor}
                onChange={handleChange}
                required
              />
            </div>

            <div className="mb-3">
              <label htmlFor="fecha_publicacion" className="form-label">Fecha de Publicación</label>
              <input
                type="date"
                className="form-control"
                id="fecha_publicacion"
                name="fecha_publicacion"
                value={formData.fecha_publicacion?.split('T')[0] || ''}
                onChange={handleChange}
                required
              />
            </div>

            <div className="mb-4">
              <label htmlFor="genero" className="form-label">Género</label>
              <input
                type="text"
                className="form-control"
                id="genero"
                name="genero"
                value={formData.genero || ''}
                onChange={handleChange}
                required
              />
            </div>

            <div className="d-flex justify-content-between">
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => navigate('/libros')}
              >
                Cancelar
              </button>
              <button type="submit" className="btn btn-warning text-white">
                Actualizar Libro
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default EditarLibro;
