import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { libroService } from '../services/libroService';
import '../styles/pages/EditarLibro.css';

const EditarLibro = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [showError, setShowError] = useState(false);
  const [errors, setErrors] = useState({});
  const [formData, setFormData] = useState({
    titulo: '',
    autor: '',
    fecha_publicacion: '',
    genero: ''
  });

  useEffect(() => {
    const cargarLibro = async () => {
      try {
        const response = await libroService.getById(id);
        if (response && response.success) {
          setFormData(response.data);
        } else {
          setError('No se pudo cargar la información del libro');
        }
      } catch (err) {
        console.error('Error al cargar el libro:', err);
        setError('Error al cargar el libro');
      } finally {
        setLoading(false);
      }
    };

    cargarLibro();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Limpiar el mensaje de error del campo que se está modificando
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }

    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const validateForm = () => {
    const newErrors = {};
    let isValid = true;

    if (!formData.titulo.trim()) {
      newErrors.titulo = 'El título es requerido';
      isValid = false;
    }

    if (!formData.autor.trim()) {
      newErrors.autor = 'El autor es requerido';
      isValid = false;
    }

    if (!formData.fecha_publicacion) {
      newErrors.fecha_publicacion = 'La fecha de publicación es requerida';
      isValid = false;
    }

    if (!formData.genero.trim()) {
      newErrors.genero = 'El género es requerido';
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = async (e) => {
    // Prevenir el comportamiento por defecto del formulario
    if (e) {
      e.preventDefault();
      e.stopPropagation();
      e.returnValue = false;
      e.cancelBubble = true;

      // Detener cualquier otro manejador de eventos
      e.stopImmediatePropagation();
    }

    // Limpiar errores anteriores
    setError(null);
    setErrors({});
    setShowError(false);

    // Validar el formulario
    if (!validateForm()) {
      return false;
    }

    try {
      const response = await libroService.update(id, formData);
      if (response && response.success) {
        setSuccess(true);
        setError(null);
        setShowError(false);

        // Ocultar el mensaje después de 2 segundos
        const timer = setTimeout(() => {
          setSuccess(false);
          clearTimeout(timer);
        }, 2000);

        return false;
      }
      throw new Error('No se pudo actualizar el libro');
    } catch (err) {
      console.error('Error al actualizar el libro:', err);

      // Mostrar la respuesta del error en consola para depuración
      console.log('Respuesta del servidor:', err.response?.data);

      // Manejar errores de validación del backend
      if (err.response?.data) {
        const errorData = err.response.data;

        // Si el error tiene un campo específico (ej: 'titulo'), lo asignamos a errors
        if (errorData.field) {
          setErrors(prev => ({
            ...prev,
            [errorData.field]: errorData.message
          }));
        } 
        // Si es un mensaje de error general
        else if (errorData.message) {
          setError(errorData.message);
          setShowError(true);
        } 
        // Si el error viene en otro formato
        else {
          setError(JSON.stringify(errorData));
          setShowError(true);
        }
      } else {
        // Mostrar el mensaje de error genérico si no hay respuesta del servidor
        const errorMsg = err.message || 'Error al actualizar el libro';
        console.log('Error genérico:', errorMsg);
        setError(errorMsg);
        setShowError(true);
      }

      return false; // Importante: Evitar cualquier comportamiento por defecto
    }
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Cargando...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger" role="alert">
        {error}
      </div>
    );
  }

  // Función para manejar el envío del formulario
  const handleFormSubmit = (e) => {
    e.preventDefault();
    e.stopPropagation();
    handleSubmit(e);
    return false;
  };

  return (
    <div className="editar-libro">
      <div className="editar-card">
        <div className="editar-header">
          <h2>Editar Libro</h2>
          {success && (
            <div className="alert alert-success mt-2 mb-0">
              ¡Libro actualizado correctamente!
            </div>
          )}
          {/* Mostrar mensaje de error general */}
          {showError && error && (
            <div className="alert alert-danger mt-3 d-flex align-items-center justify-content-between">
              <div className="error-message w-100">
                {error}
              </div>
              <button 
                type="button" 
                className="btn-close ms-2" 
                onClick={(e) => {
                  e.preventDefault();
                  setShowError(false);
                }}
                aria-label="Cerrar"
              ></button>
            </div>
          )}
        </div>

        <div className="editar-body">
          <form onSubmit={handleFormSubmit} noValidate>
            <div className="mb-3">
              <label htmlFor="titulo" className="form-label">Título</label>
              <input
                type="text"
                className={`form-control ${errors.titulo ? 'is-invalid' : ''}`}
                id="titulo"
                name="titulo"
                value={formData.titulo}
                onChange={handleChange}
                required
              />
              {errors.titulo && (
                <div className="error-message mt-1">
                  {errors.titulo}
                </div>
              )}
            </div>

            <div className="mb-3">
              <label htmlFor="autor" className="form-label">Autor</label>
              <input
                type="text"
                className={`form-control ${errors.autor ? 'is-invalid' : ''}`}
                id="autor"
                name="autor"
                value={formData.autor}
                onChange={handleChange}
                required
              />
              {errors.autor && <div className="invalid-feedback">{errors.autor[0] || errors.autor}</div>}
            </div>

            <div className="mb-3">
              <label htmlFor="fecha_publicacion" className="form-label">Fecha de Publicación</label>
              <input
                type="date"
                className={`form-control ${errors.fecha_publicacion ? 'is-invalid' : ''}`}
                id="fecha_publicacion"
                name="fecha_publicacion"
                value={formData.fecha_publicacion}
                onChange={handleChange}
                required
              />
              {errors.fecha_publicacion && <div className="invalid-feedback">{errors.fecha_publicacion[0] || errors.fecha_publicacion}</div>}
            </div>

            <div className="mb-4">
              <label htmlFor="genero" className="form-label">Género</label>
              <input
                type="text"
                className={`form-control ${errors.genero ? 'is-invalid' : ''}`}
                id="genero"
                name="genero"
                value={formData.genero}
                onChange={handleChange}
                required
              />
              {errors.genero && <div className="invalid-feedback">{errors.genero[0] || errors.genero}</div>}
            </div>

            <div className="d-flex justify-content-between">
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => navigate('/libros')}
              >
                Cancelar
              </button>
              <button type="submit" className="btn btn-warning text-white">
                Actualizar Libro
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default EditarLibro;
