import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { libroService } from '../services/libroService';
import '../styles/pages/DetalleLibro.css';

const DetalleLibro = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [libro, setLibro] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const cargarLibro = async () => {
      try {
        const data = await libroService.getById(id);
        setLibro(data);
      } catch (err) {
        console.error('Error al cargar el libro:', err);
        setError('No se pudo cargar la información del libro');
      } finally {
        setLoading(false);
      }
    };

    cargarLibro();
  }, [id]);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Cargando...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-5">
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
        <button 
          className="btn btn-primary mt-3"
          onClick={() => navigate('/libros')}
        >
          Volver a la lista
        </button>
      </div>
    );
  }

  if (!libro) {
    return (
      <div className="container mt-5">
        <h2>Libro no encontrado</h2>
        <button 
          className="btn btn-primary mt-3"
          onClick={() => navigate('/libros')}
        >
          Volver a la lista
        </button>
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header bg-primary text-white">
              <h2 className="mb-0">{libro.titulo}</h2>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-md-4">
                  <div className="text-center mb-3">
                    <img 
                      src={libro.imagenUrl || 'https://via.placeholder.com/200x300?text=Sin+imagen'} 
                      alt={libro.titulo} 
                      className="img-fluid rounded"
                      style={{ maxHeight: '300px' }}
                    />
                  </div>
                </div>
                <div className="col-md-8">
                  <h4 className="card-title">{libro.titulo}</h4>
                  <p className="text-muted">por {libro.autor || 'Autor desconocido'}</p>

                  <div className="mb-3">
                    <h5>Descripción</h5>
                    <p className="card-text">
                      {libro.descripcion || 'No hay descripción disponible para este libro.'}
                    </p>
                  </div>

                  <div className="row">
                    <div className="col-md-6">
                      <p><strong>Género:</strong> {libro.genero || 'No especificado'}</p>
                      <p><strong>Editorial:</strong> {libro.editorial || 'No especificada'}</p>
                    </div>
                    <div className="col-md-6">
                      <p><strong>Año de publicación:</strong> {libro.anioPublicacion || 'No especificado'}</p>
                      <p><strong>ISBN:</strong> {libro.isbn || 'No disponible'}</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="card-footer text-muted">
              <button 
                className="btn btn-secondary me-2"
                onClick={() => navigate('/libros')}
              >
                Volver
              </button>
              <button 
                className="btn btn-primary me-2"
                onClick={() => navigate(`/libros/editar/${libro.id}`)}
              >
                Editar
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DetalleLibro;
