import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { libroService } from '../services/libroService';
import '../styles/pages/EditarLibro.css';

const EditarLibro = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [showError, setShowError] = useState(false);
  const [errors, setErrors] = useState({});
  const [formData, setFormData] = useState({
    titulo: '',
    autor: '',
    fecha_publicacion: '',
    genero: ''
  });

  useEffect(() => {
    const cargarLibro = async () => {
      try {
        const response = await libroService.getById(id);
        if (response && response.success) {
          setFormData(response.data);
        } else {
          setError('No se pudo cargar la información del libro');
        }
      } catch (err) {
        console.error('Error al cargar el libro:', err);
        setError('Error al cargar el libro');
      } finally {
        setLoading(false);
      }
    };

    cargarLibro();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Limpiar el mensaje de error del campo que se está modificando
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }

    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const validateForm = () => {
    const newErrors = {};
    let isValid = true;

    if (!formData.titulo.trim()) {
      newErrors.titulo = 'El título es requerido';
      isValid = false;
    }

    if (!formData.autor.trim()) {
      newErrors.autor = 'El autor es requerido';
      isValid = false;
    }

    if (!formData.fecha_publicacion) {
      newErrors.fecha_publicacion = 'La fecha de publicación es requerida';
      isValid = false;
    }

    if (!formData.genero.trim()) {
      newErrors.genero = 'El género es requerido';
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      const response = await libroService.update(id, formData);
      if (response && response.success) {
        setSuccess(true);
        setError(null);
        setShowError(false);

        // Ocultar el mensaje después de 2 segundos
        setTimeout(() => {
          setSuccess(false);
        }, 2000);
      } else {
        throw new Error('No se pudo actualizar el libro');
      }
    } catch (err) {
      console.error('Error al actualizar el libro:', err);
      setError(err.message || 'Error al actualizar el libro');
      setShowError(true);

      // Ocultar el mensaje de error después de 5 segundos
      setTimeout(() => {
        setShowError(false);
      }, 5000);
    }
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Cargando...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger" role="alert">
        {error}
      </div>
    );
  }

  return (
    <>
      {success && (
        <div className="toast-container">
          <div className="toast-success">
            ¡Libro actualizado correctamente!
          </div>
        </div>
      )}
      {showError && error && (
        <div className="toast-container">
          <div className="toast-error">
            {error}
          </div>
        </div>
      )}
      <div className="editar-libro">
        <div className="editar-card">
          <div className="editar-header">
            <h2>Editar Libro</h2>
          </div>

          <div className="editar-body">
            {error && <div className="alert alert-danger">{error}</div>}

          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="titulo" className="form-label">Título</label>
              <input
                type="text"
                className={`form-control ${errors.titulo ? 'is-invalid' : ''}`}
                id="titulo"
                name="titulo"
                value={formData.titulo}
                onChange={handleChange}
              />
              {errors.titulo && <div className="error-message">{errors.titulo}</div>}
            </div>

            <div className="mb-3">
              <label htmlFor="autor" className="form-label">Autor</label>
              <input
                type="text"
                className={`form-control ${errors.autor ? 'is-invalid' : ''}`}
                id="autor"
                name="autor"
                value={formData.autor}
                onChange={handleChange}
              />
              {errors.autor && <div className="error-message">{errors.autor}</div>}
            </div>

            <div className="mb-3">
              <label htmlFor="fecha_publicacion" className="form-label">Fecha de Publicación</label>
              <input
                type="date"
                className={`form-control ${errors.fecha_publicacion ? 'is-invalid' : ''}`}
                id="fecha_publicacion"
                name="fecha_publicacion"
                value={formData.fecha_publicacion?.split('T')[0] || ''}
                onChange={handleChange}
              />
              {errors.fecha_publicacion && (
                <div className="error-message">{errors.fecha_publicacion}</div>
              )}
            </div>

            <div className="mb-4">
              <label htmlFor="genero" className="form-label">Género</label>
              <input
                type="text"
                className={`form-control ${errors.genero ? 'is-invalid' : ''}`}
                id="genero"
                name="genero"
                value={formData.genero || ''}
                onChange={handleChange}
              />
              {errors.genero && <div className="error-message">{errors.genero}</div>}
            </div>

            <div className="d-flex justify-content-between">
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => navigate('/libros')}
              >
                Cancelar
              </button>
              <button type="submit" className="btn btn-warning text-white">
                Actualizar Libro
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
    </>
  );
};

export default EditarLibro;
